catalog_script_client.script:
function onLoad() {
	function buscaDados (key){
		var ga = new GlideAjax('HandleCandidatos');
		ga.addParam('sysparm_name','Search');
		ga.addParam('sysparm_key', key); 
		ga.getXML(getAnswer);  

		function getAnswer(response) {  
			var answer = response.responseXML.documentElement.getAttribute("answer"); 

				data = JSON.parse(answer);
				// alert(data.nome);
				g_form.setValue('nome', data.nome);
				g_form.setValue('email', data.email);
		}
	}

	var url = location.href;
	var regex = /acesskey=([^&]*)/;
	var match = url.match(regex);

	if (match) {
		// alert(match[1]);
		g_form.setValue('key', match[1]);
		var key = match[1];

		//Busca dados
		buscaDados(key);


   //Type appropriate comment here, and begin script below

	}



}
