function renderApproveReject(img, response, emailbody){
	var instancia = gs.getProperty("instance_name2");
	var url = "https://dev218783.service-now.com";
	var mark = email.watermark;
	var number = current.number;
	var emailAdress = instancia + "@service-now.com";

	if(current.getTableName().indexOf("sysapproval") != -1) {
		number = current.sysapproval.number;
	}

	// var mailLink = '<a href="mailto:'+ emailAdress + '?subject=Re:' + number + ' ' + response +'&body=' +  emailbody +  mark + '"><img src="' + url + image + '"></a>';

	var mailLink = '<a href="mailto:' + emailAdress + '?subject=Re:' + number + ' ' + response + '&body=' + emailbody + mark + '"><img height="100" src="' + url + img + '"></a>';


	return mailLink;
}
