sys_script_include.script:
var HandleCandidatos = Class.create();
HandleCandidatos.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	Search: function(key){
		var info = {};
		key = this.getParameter("sysparm_key");
		gs.info('KEY PASSADA NO CLIENT:' + key);

		var gr = new GlideRecord('x_1174267_case_b_0_candidatos');
		gr.addQuery('chave_de_acesso', key);
		gr.query();
		if(gr.next()){
			info = {
					nome: gr.getDisplayValue('nome'),
					email: gr.getDisplayValue('email')
			};
			return JSON.stringify(info);
		}
	},

	Test: function (key){
		obj = this.Search(key);
		return gs.info('nome:' + obj.nome + 'email:' + obj.email);

	},

	UpdateGestores: function (){
		var gr = "";
	},

    type: 'HandleCandidatos'
});
