sys_script_include.script:
var ApiViaCep = Class.create();
ApiViaCep.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCepInfo: function (cep){
		var api = new sn_ws.RESTMessageV2();
		var cepParm = this.getParameter('sysparm_cep');
		gs.info('CEP PASSADO DO FRONT PRO BACK FOI:' + cepParm);
        api.setEndpoint("https://viacep.com.br/ws/${cep}/json");
		api.setStringParameter('cep', cepParm);
        api.setHttpMethod("GET");
        var response = api.execute();
		var info = response.getBody();
		var obj = JSON.parse(info);
		gs.info(JSON.stringify(obj));
		return JSON.stringify(obj);

		},
    type: 'ApiViaCep'
});
