sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

// var texto = email.body_text;

const string = `${email.body_text}`;

const expectativaSalarial = string.match(/Qual expectativa salarial: (.+)/)[1];
const projetoImportante = string.match(/Fale sobre um projeto importante que você trabalhou: (.+)/)[1];
const motivoTechHire = string.match(/Por que você quer trabalhar na Tech Hire: (.+)/)[1];

if(email.from == current.email){
	current.perguntas_respondidas = "sim";
	current.pergunta_adicional_1 = expectativaSalarial;
	current.pergunta_adicional_2 = projetoImportante;
	current.pergunta_adicional_3 = motivoTechHire;

	current.update();

}


})(current, event, email, logger, classifier);
